package com.sales.vo;

import java.util.List;

import com.sales.vo.base.AddressInfo;
import com.sales.vo.base.OrderCancelReason;
import com.sales.vo.base.OrderGoods;
import com.sales.vo.base.SalesLoginedReq;
import com.sales.vo.base.MSG_TYPES;

public class ModifyOrderReq extends SalesLoginedReq {

	public ModifyOrderReq() {
		super(MSG_TYPES.MSG_MODIFY_ORDER);
		// TODO Auto-generated constructor stub
	}
	
	private String orderid = null;
	private String action = null;
	private List<OrderGoods> goodlist = null;
	private String committoken = null;
	private OrderCancelReason ordercancelreason = null;
	private AddressInfo addressinfo = null;

	public String getOrderid() {
		return orderid;
	}

	public void setOrderid(String orderid) {
		this.orderid = orderid;
	}
		
	public String getAction() {
		return action;
	}

	public void setAction(String action) {
		this.action = action;
	}

	public List<OrderGoods> getGoodlist() {
		return goodlist;
	}

	public void setGoodlist(List<OrderGoods> goodlist) {
		this.goodlist = goodlist;
	}

	public String getCommittoken() {
		return committoken;
	}

	public void setCommittoken(String committoken) {
		this.committoken = committoken;
	}

	public OrderCancelReason getOrdercancelreason() {
		return ordercancelreason;
	}

	public void setOrdercancelreason(OrderCancelReason ordercancelreason) {
		this.ordercancelreason = ordercancelreason;
	}

	public AddressInfo getAddressinfo() {
		return addressinfo;
	}

	public void setAddressinfo(AddressInfo addressinfo) {
		this.addressinfo = addressinfo;
	}
	
	
	
	
}
